import axios from 'axios'

const baseUrl = 'http://localhost:3001/persons';

const getPersons = () => (
  axios.get(baseUrl).then(response => {
    return response.data;
  })
)

const addPerson = () => (
  axios.post(``)
)

export default { getPersons, addPerson };
