const Notification = ({ message }) => {
  if (!message) {
    return null
  }

  const notificationStyle = {
    color: 'red',
    borderStyle: 'solid',
    borderColor: 'red'
  }

  return <p style={notificationStyle}>{message}</p>
}



export default Notification;
