const Notification = ({ message, successAction }) => {
  if (!message) {
    return null
  }

  let color = successAction ? 'green' : red

  const notificationStyle = {
    color: 'green',
    borderStyle: 'solid',
    borderColor: 'green',
    borderRadius: 5,
    padding: 10,
  }

  return <p style={notificationStyle}>{message}</p>
}



export default Notification;
