import { useState } from 'react'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'

const Person = ({ name, number }) => {
  return (<p>{name} {number}</p>);
}

const App = () => {
  console.log('rendering...');

  const [persons, setPersons] = useState([]);

  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [personsToDisplay, setPersonsToDisplay] = useState(persons);

  const handleNewName = event => {
    setNewName(event.target.value);
  }

  const handleNewNumber = event => {
    setNewNumber(event.target.value);
  }

  const addPerson = event => {
    event.preventDefault();

    if (persons.map(person => person.name).includes(newName)) {
      alert(`${newName} is already added to the phonebook`);
    } else {
      const newPersons = persons.concat(
        { id: persons.length, name: newName, number: newNumber }
      );

      setPersons(newPersons);
      setPersonsToDisplay(newPersons);
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter persons={persons} setPersonsToDisplay={setPersonsToDisplay} />
      <h2>Add new contact</h2>
      <PersonForm 
        newName={newName}
        newNumber={newNumber}
        handleNewName={handleNewName}
        handleNewNumber={handleNewNumber}
        onSubmit={addPerson}
      />
      <h2>Numbers</h2>
      <Persons personsToDisplay={personsToDisplay} />
    </div>
  )
}

export default App
