import { useState } from 'react'

const Person = ({ name, number }) => {
  return (<p>{name} {number}</p>);
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ]);

  const [personsToDisplay, setPersonsToDisplay] = useState(persons)

  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [newSearch, setNewSearch] = useState('');

  const addContact = (name, number) => {
    setPersons(persons.concat({ id: persons.length, name, number }));
  }

  const handleNewName = event => {
    setNewName(event.target.value);
  }

  const handleNewNumber = event => {
    setNewNumber(event.target.value);
  }

  const handleNewSearch = event => {
    const search = event.target.value;

    setNewSearch(search);

    setPersonsToDisplay(persons.filter(person => {
      const regexp = new RegExp(`^${search}`, 'i');
      return person.name.match(regexp);
    }));
  }

  const handleSubmit = event => {
    event.preventDefault();

    if (persons.map(person => person.name).includes(newName)) {
      alert(`${newName} is already added to the phonebook`);
    } else {
      addContact(newName, newNumber);
    }
  }

  const Filter = () => {
    const [newSearch, setNewSearch] = useState('');

    <>
      search: <input onChange={handleNewSearch} value={newSearch} />
    </>
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <h2>Add new contact</h2>
      <form>
        <div>
          name: <input onChange={handleNewName} value={newName} />
        </div>
        <div>
          number: <input onChange={handleNewNumber} value={newNumber} />
        </div>
        <div>
          <button onClick={handleSubmit} type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {personsToDisplay.map(person => (
          <Person key={person.id} name={person.name} number={person.number} />
        ))}
      </div>
    </div>
  )
}

export default App
