import { useState } from 'react'

const Person = ({ name }) => <p>{name}</p>

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', id: 0 }
  ]);

  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('whats their number')

  const addContact = name => {
    setPersons(persons.concat({ name, id: persons.length }));
  }

  const handleInput = event => {
    setNewName(event.target.value);
  }

  const handleSubmit = event => {
    event.preventDefault();

    if (persons.map(person => person.name).includes(newName)) {
      alert(`${newName} is already added to the phonebook`);
    } else {
      addContact(newName);
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          name: <input onChange={handleInput} value={newName} />
        </div>
        <div>
          number: <input onChange={handleInput} value={newNumber} />
        </div>
        <div>
          <button onClick={handleSubmit} type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map(person => <Person key={person.id} name={person.name} />)}
    </div>
  )
}

export default App
